mapper.select:
SELECT o.id AS orderId, o.created_time AS createdTime, o.is_comment as isComment, o.pay_method AS payMethod, o.real_pay_amount AS realPayAmount, o.post_amount AS postAmount, os.order_status AS orderStatus FROM orders o LEFT JOIN order_status AS os ON o.id = os.order_id WHERE o.user_id = #{paramsMap.userId} AND o.is_delete = 0
And os.order_status = #{paramsMap.orderStatus}
ORDER BY o.updated_time
